import LoginForm from './_components/LoginForm';

/**
 * Login Page - Student Portal
 * Displays login form for students to access the School Management System
 */
function Login() {
  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-gray-50 via-blue-50 to-gray-100 px-4">
      <div className="w-full max-w-md">
        {/* Login Card */}
        <div className="bg-white rounded-2xl shadow-2xl p-8 md:p-10">
          {/* Logo Section */}
          <div className="flex flex-col items-center mb-6">
            <div className="w-20 h-20 mb-4 rounded-full bg-blue-100 flex items-center justify-center">
              <img
                src="/schuler_Logo.png"
                alt="School Logo"
                className="w-16 h-16 object-contain"
                onError={(e) => {
                  e.target.style.display = 'none';
                  e.target.parentElement.innerHTML = `
                    <svg class="w-12 h-12 text-blue-600" fill="currentColor" viewBox="0 0 20 20">
                      <path d="M10.394 2.08a1 1 0 00-.788 0l-7 3a1 1 0 000 1.84L5.25 8.051a.999.999 0 01.356-.257l4-1.714a1 1 0 11.788 1.838L7.667 9.088l1.94.831a1 1 0 00.787 0l7-3a1 1 0 000-1.838l-7-3zM3.31 9.397L5 10.12v4.102a8.969 8.969 0 00-1.05-.174 1 1 0 01-.89-.89 11.115 11.115 0 01.25-3.762zM9.3 16.573A9.026 9.026 0 007 14.935v-3.957l1.818.78a3 3 0 002.364 0l5.508-2.361a11.026 11.026 0 01.25 3.762 1 1 0 01-.89.89 8.968 8.968 0 00-5.35 2.524 1 1 0 01-1.4 0zM6 18a1 1 0 001-1v-2.065a8.935 8.935 0 00-2-.712V17a1 1 0 001 1z"/>
                    </svg>
                  `;
                }}
              />
            </div>
            <div className="text-center">
              <h3 className="text-lg font-semibold text-gray-800">
                School Management System
              </h3>
            </div>
          </div>

          {/* Page Title */}
          <h1 className="text-2xl font-bold text-blue-600 text-center mb-8">
            Student Portal
          </h1>

          {/* Login Form */}
          <LoginForm />
        </div>

        {/* Footer */}
        <div className="text-center mt-6 text-sm text-gray-600">
          <p>Powered by</p>
          <p className="font-semibold text-gray-800 mt-1">Sch√ºler Track</p>
        </div>
      </div>
    </div>
  );
}

export default Login;
