import LoginForm from './_components/LoginForm';

/**
 * Login Page - Student Portal
 * Displays login form for students to access the School Management System
 */
function Login() {
  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-teal-50 via-blue-50 to-gray-100 px-4">
      <div className="w-full max-w-lg">
        {/* Login Card */}
        <div className="bg-white rounded-2xl shadow-xl p-10">
          {/* Logo and Title Section */}
          <div className="flex items-center justify-center gap-4 mb-2">
            <img
              src="/image.png"
              alt="School Logo"
              className="w-24 h-24 object-contain flex-shrink-0"
              onError={(e) => {
                e.target.style.display = 'none';
              }}
            />
            <h3 className="text-4xl font-bold text-gray-700 leading-tight">
              School<br />Management<br />System
            </h3>
          </div>

          {/* Student Portal Title */}
          <h1 className="text-3xl font-bold text-blue-600 text-center mb-8">
            Student Portal
          </h1>

          {/* Login Form */}
          <LoginForm />
        </div>

        {/* Footer */}
        <div className="text-center mt-6 text-sm text-gray-700">
          <p className="mb-1">Powered by</p>
          <p className="font-semibold text-gray-900">Sch√ºler Track</p>
        </div>
      </div>
    </div>
  );
}

export default Login;
