import { useState } from 'react';
import { Stack, Anchor, Text } from '@mantine/core';
import Input from '../../../components/ui/Input';
import Button from '../../../components/ui/Button';

/**
 * LoginForm component for the Student Portal
 * Handles student login with student number and password
 */
function LoginForm() {
  const [studentNumber, setStudentNumber] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({
    studentNumber: '',
    password: ''
  });

  /**
   * Validates form fields
   * @returns {boolean} Whether form is valid
   */
  const validateForm = () => {
    const newErrors = {
      studentNumber: '',
      password: ''
    };

    if (!studentNumber.trim()) {
      newErrors.studentNumber = 'Student number is required';
    }

    if (!password) {
      newErrors.password = 'Password is required';
    }

    setErrors(newErrors);
    return !newErrors.studentNumber && !newErrors.password;
  };

  /**
   * Handles form submission
   * @param {Event} e - Form submit event
   */
  const handleSubmit = (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    // Simulate loading state
    setLoading(true);

    // Simulate API call
    setTimeout(() => {
      console.log('Login attempt:', { studentNumber, password });
      setLoading(false);
      // TODO: Add actual login logic when backend is ready
    }, 2000);
  };

  /**
   * Handles forgot password click
   */
  const handleForgotPassword = () => {
    console.log('Forgot password clicked');
    // TODO: Add forgot password logic when backend is ready
  };

  return (
    <form onSubmit={handleSubmit}>
      <Stack gap="md">
        <Input
          type="text"
          label="Student Number"
          placeholder="Enter your student number"
          required
          value={studentNumber}
          onChange={(e) => setStudentNumber(e.target.value)}
          error={errors.studentNumber}
          disabled={loading}
        />

        <Input
          type="password"
          label="Password"
          placeholder="Enter your password"
          required
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          error={errors.password}
          disabled={loading}
        />

        <Anchor
          component="button"
          type="button"
          size="sm"
          onClick={handleForgotPassword}
          style={{ textAlign: 'right', fontSize: '14px' }}
          disabled={loading}
        >
          Forgot Password?
        </Anchor>

        <Button
          type="submit"
          fullWidth
          loading={loading}
        >
          {loading ? 'Logging in...' : 'Log In'}
        </Button>

        <Text size="sm" c="dimmed" style={{ textAlign: 'center', fontSize: '14px' }}>
          Note: Temporary identifiers (PIN) sent to your email serve as your login credentials.
        </Text>
      </Stack>
    </form>
  );
}

export default LoginForm;
