import { Button as MantineButton } from '@mantine/core';

/**
 * Reusable Button component with Mantine
 * @param {ReactNode} children - Button content
 * @param {string} variant - Button variant (filled, outline, light, etc.)
 * @param {string} size - Button size (xs, sm, md, lg, xl)
 * @param {boolean} loading - Loading state
 * @param {boolean} disabled - Disabled state
 * @param {function} onClick - Click handler
 * @param {string} type - Button type (button, submit, reset)
 * @param {boolean} fullWidth - Whether button should be full width
 * @param {object} rest - Other props
 */
function Button({ 
  children, 
  variant = 'filled', 
  size = 'md', 
  loading = false,
  disabled = false,
  onClick,
  type = 'button',
  fullWidth = false,
  ...rest 
}) {
  return (
    <MantineButton
      variant={variant}
      size={size}
      loading={loading}
      disabled={disabled}
      onClick={onClick}
      type={type}
      fullWidth={fullWidth}
      styles={{
        root: { 
          fontSize: '16px',
          height: '44px',
          fontWeight: 600
        }
      }}
      {...rest}
    >
      {children}
    </MantineButton>
  );
}

export default Button;
