import { Container, Paper, Title, Stack, Image, Box, Text } from '@mantine/core';
import LoginForm from './_components/LoginForm';

/**
 * Login Page - Student Portal
 * Displays login form for students to access the School Management System
 */
function Login(): JSX.Element {
  return (
    <main>
      <Box
        style={{
          minHeight: '100vh',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          background: 'linear-gradient(135deg, #f0fdfa 0%, #ffffff 50%, #f3f4f6 100%)',
          padding: '20px'
        }}
      >
      <Container size="sm" style={{ width: '100%', maxWidth: '660px' }}>
        <Paper
          p={32}
          radius={30}
          style={{
            width: '660px',
            maxWidth: '100%',
            backgroundColor: '#fff',
            boxShadow: '0 8px 40px rgba(0, 0, 0, 0.2)',
            borderRadius: '30px',
            zIndex: 10,
          }}
        >
          <Stack gap={24} align="center">
            {/* Logo and Title Section */}
            <Box style={{ display: 'flex', alignItems: 'center', gap: '16px', marginBottom: '8px' }}>
              <Image
                src="/image.png"
                alt="School Logo"
                w={150}
                h={150}
                fit="contain"
              />
              <Title
                order={1}
                size="40px"
                fw={700}
                style={{ lineHeight: '1', textAlign: 'left', color: '#3F3F46',}}
              >
                School<br />Management<br />System
              </Title>
            </Box>

            {/* Student Portal Title */}
            <Title
              order={2}
              size="32px"
              fw={700}
              style={{ color: '#2563EB', textAlign: 'center' }}
            >
              Student Portal
            </Title>

            {/* Login Form */}
            <Box style={{ width: '100%' }}>
              <LoginForm />
            </Box>
          </Stack>
        </Paper>

        {/* Footer */}
        <Box style={{ textAlign: 'center', marginTop: '24px' }}>
          <Image
            src="/schuler_logo.png"
            alt="SchÃ¼ler Track"
            w={150}
            h="auto"
            fit="contain"
            style={{ margin: '0 auto' }}
          />
        </Box>
      </Container>
    </Box>
    </main>
  );
}

export default Login;
