import { TextInput, PasswordInput } from '@mantine/core';

/**
 * Reusable Input component with Mantine
 * @param {string} type - Input type (text, password, email, etc.)
 * @param {string} label - Label text
 * @param {string} placeholder - Placeholder text
 * @param {boolean} required - Whether field is required
 * @param {string} error - Error message
 * @param {function} onChange - Change handler
 * @param {any} value - Input value
 * @param {object} rest - Other props
 */
function Input({ 
  type = 'text', 
  label, 
  placeholder, 
  required = false, 
  error, 
  onChange,
  value,
  ...rest 
}) {
  // Use PasswordInput for password type
  if (type === 'password') {
    return (
      <PasswordInput
        label={label}
        placeholder={placeholder}
        required={required}
        error={error}
        onChange={onChange}
        value={value}
        size="md"
        styles={{
          label: { fontSize: '16px', fontWeight: 500, marginBottom: '8px' },
          input: { fontSize: '16px' }
        }}
        {...rest}
      />
    );
  }

  // Use TextInput for other types
  return (
    <TextInput
      type={type}
      label={label}
      placeholder={placeholder}
      required={required}
      error={error}
      onChange={onChange}
      value={value}
      size="md"
      styles={{
        label: { fontSize: '16px', fontWeight: 500, marginBottom: '8px' },
        input: { fontSize: '16px' }
      }}
      {...rest}
    />
  );
}

export default Input;
