import { useState } from 'react';
import Input from '../../../components/ui/Input';
import Button from '../../../components/ui/Button';

/**
 * LoginForm component for Student Portal
 * Handles student login with validation and loading state
 */
function LoginForm() {
  const [studentNumber, setStudentNumber] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({
    studentNumber: '',
    password: ''
  });

  /**
   * Validates form fields
   * @returns {boolean} Whether form is valid
   */
  const validateForm = () => {
    const newErrors = {
      studentNumber: '',
      password: ''
    };

    if (!studentNumber.trim()) {
      newErrors.studentNumber = 'Student number is required';
    }

    if (!password) {
      newErrors.password = 'Password is required';
    }

    setErrors(newErrors);
    return !newErrors.studentNumber && !newErrors.password;
  };

  /**
   * Handles form submission
   * @param {Event} e - Form submit event
   */
  const handleSubmit = (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    // Simulate loading state
    setLoading(true);

    // Simulate login (2 seconds)
    setTimeout(() => {
      console.log('Login attempt:', { studentNumber, password });
      setLoading(false);
      
      // Reset form
      setStudentNumber('');
      setPassword('');
      
      // TODO: Add actual login logic when backend is ready
    }, 2000);
  };

  /**
   * Handles forgot password click
   */
  const handleForgotPassword = () => {
    console.log('Forgot password clicked');
    // TODO: Add forgot password logic when backend is ready
  };

  return (
    <form onSubmit={handleSubmit} className="w-full space-y-4">
      <Input
        type="text"
        label="Student Number"
        placeholder="Enter your student number"
        required
        value={studentNumber}
        onChange={(e) => setStudentNumber(e.target.value)}
        error={errors.studentNumber}
        disabled={loading}
      />

      <Input
        type="password"
        label="Password"
        placeholder="Enter your password"
        required
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        error={errors.password}
        disabled={loading}
      />

      <div className="text-right">
        <button
          type="button"
          onClick={handleForgotPassword}
          disabled={loading}
          className="text-sm text-blue-600 hover:text-blue-800 hover:underline focus:outline-none focus:ring-2 focus:ring-blue-500 rounded px-1"
        >
          Forgot Password?
        </button>
      </div>

      <Button
        type="submit"
        fullWidth
        loading={loading}
      >
        {loading ? 'Logging in...' : 'Log In'}
      </Button>

      <p className="text-sm text-gray-600 text-center mt-4">
        Note: Temporary identifiers (OTP) sent to your email serve as your login credentials.
      </p>
    </form>
  );
}

export default LoginForm;
