import { useState } from 'react';
import { TextInput, PasswordInput, Button, Anchor, Stack, Text } from '@mantine/core';
import { User, Lock } from 'lucide-react';

/**
 * LoginForm component for Student Portal
 * Handles student login with validation and loading state
 */
function LoginForm() {
  const [studentNumber, setStudentNumber] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({
    studentNumber: '',
    password: ''
  });

  /**
   * Validates form fields
   * @returns {boolean} Whether form is valid
   */
  const validateForm = () => {
    const newErrors = {
      studentNumber: '',
      password: ''
    };

    if (!studentNumber.trim()) {
      newErrors.studentNumber = 'Student number is required';
    }

    if (!password) {
      newErrors.password = 'Password is required';
    }

    setErrors(newErrors);
    return !newErrors.studentNumber && !newErrors.password;
  };

  /**
   * Handles form submission
   * @param {Event} e - Form submit event
   */
  const handleSubmit = (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    // Simulate loading state
    setLoading(true);

    // Simulate login (2 seconds)
    setTimeout(() => {
      console.log('Login attempt:', { studentNumber, password });
      setLoading(false);
      
      // Reset form
      setStudentNumber('');
      setPassword('');
      setErrors({ studentNumber: '', password: '' });
      
      // TODO: Add actual login logic when backend is ready
    }, 2000);
  };

  /**
   * Handles forgot password click
   */
  const handleForgotPassword = () => {
    console.log('Forgot password clicked');
    // TODO: Add forgot password logic when backend is ready
  };

  return (
    <form onSubmit={handleSubmit}>
      <Stack gap={16}>
        <TextInput
          label="Student Number"
          placeholder="Enter your student number"
          required
          value={studentNumber}
          onChange={(e) => setStudentNumber(e.target.value)}
          error={errors.studentNumber}
          disabled={loading}
          leftSection={<User size={18} />}
          autoComplete="username"
          size="md"
          radius="md"
        />

        <PasswordInput
          label="Password"
          placeholder="Enter your password"
          required
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          error={errors.password}
          disabled={loading}
          leftSection={<Lock size={18} />}
          autoComplete="current-password"
          size="md"
          radius="md"
        />

        <Anchor
          component="button"
          type="button"
          size="sm"
          onClick={handleForgotPassword}
          disabled={loading}
          style={{ textAlign: 'right', color: '#2563EB' }}
        >
          Forgot Password?
        </Anchor>

        <Button
          type="submit"
          fullWidth
          loading={loading}
          size="md"
          radius="md"
          style={{ backgroundColor: '#ffffff' }}
        >
          {loading ? 'Logging in...' : 'Log In'}
        </Button>

        <Text size="sm" c="dimmed" ta="center" style={{ whiteSpace: 'nowrap' }}>
          Note: Temporary identifiers (OTP) sent to your email serve as your login credentials.
        </Text>
      </Stack>
    </form>
  );
}

export default LoginForm;
